function createHeadings(e,t=null){window.addEventListener("load",(()=>{wrapDivs(document.querySelector(e),1,t)}))}function wrapDivs(e,t=1,n=null){const i=e.getAttribute("data-heading");if(i){const o=document.createElement(`h${t}`);o.textContent=n?`${n} ${i}`:i,e.insertBefore(o,e.firstChild)}e.querySelectorAll(":scope > div[data-heading]").forEach(((e,i)=>{wrapDivs(e,t+1,n?`${n}.${i+1}`:null)}))}document.addEventListener("DOMContentLoaded",(function(){for(el of document.querySelectorAll("[ctc]"))el.title="Click to copy",el.addEventListener("click",(e=>{e.preventDefault(),navigator.clipboard.writeText(e.target.innerText)}))}),!1);